import React from "react";
import styled from "styled-components";
import { reduxForm, Field } from 'redux-form';

import TextInput from "./Form/TextInput"
import RadioInput from "./Form/RadioInput";
import DateInput from "./Form/DatepickerInput";

const StyledForm = styled.form`
    width: 600px;
    background-color: grey;
    margin: auto;
    text-align: left;
`;
const StyledField = styled(Field)``;
const StyledInput = styled.input``;

export const ButtonContainer = styled.div`
  text-align: center;
`;

class RegistrationForm extends React.Component {
    render() {
        return (
        <StyledForm noValidate={true}>
            {/* <TextInput label="firstname" placeholder="Required" type="text"/>
            <TextInput label="firstname" placeholder="Required" type="text"/>
            <TextInput label="lastname" placeholder="Required" type="text"/>
            <DateInput label="lastname" />
            <RadioInput label="gender" options={["female", "male"]} />
            <TextInput label="email" placeholder="Required" type="text"/> */}
            <StyledField 
                label="mobile" 
                placeholder="Required" 
                type="text"
                component={TextInput}
            />
            <StyledField 
                label="firstname" 
                placeholder="Required" 
                type="text"
                component={TextInput}
            />
            <StyledField 
                label="lastname" 
                placeholder="Required" 
                type="text"
                component={TextInput}
            />
            <StyledField 
                label="birthday" 
                component={DateInput}
            />
            <StyledField 
                label="gender" 
                component={RadioInput}
                options={["female", "male"]}
            />
            <StyledField 
                label="email" 
                placeholder="Required" 
                type="text"
                component={TextInput}
            />
            <ButtonContainer>
              <StyledButton
                type="submit"
                text={isSubmitting ? <LoadingIndicator /> : "Save"}
                isDisabled={isSubmitting} />
            </ButtonContainer>
        </StyledForm>
        );
    }
}

export default reduxForm({
    form: 'registration',
    destroyOnUnmount: true
  })(RegistrationForm);
// export default RegistrationForm;
