import React from "react";
import styled from "styled-components";
import { reduxForm, Field } from 'redux-form';

import TextInput from "./Form/TextInput"
import RadioInput from "./Form/RadioInput";
import DateInput from "./Form/DatepickerInput";

import { required, email } from "../utils/validate";

const StyledForm = styled.form`
    width: 600px;
    padding: 16px;
    margin: auto;
    text-align: left;
    box-shadow: 0 2px 10px 0 rgba(0, 0, 0, 0.25);
`;
const StyledField = styled(Field)``;
const StyledInput = styled.input``;

export const ButtonContainer = styled.div`
  text-align: center;
`;

const StyledButton = styled.button`
    width: 100%;
`;

class RegistrationForm extends React.Component {
    // constructor() {
    //     this.state = {
    //         mobile: "",
    //         firstname: "",
    //         lastname: "",
    //         birthday: null,
    //         gender: null,
    //         email: "",
    //         errors: []
    //     }
    // }

    renderField = ({
        input,
        label,
        type,
        meta: { touched, error, warning }
      }) => (
        <div>
          <label>{label}</label>
          <div>
            <input {...input} placeholder={label} type={type} />
            {touched &&
              ((error && <span>{error}</span>) ||
                (warning && <span>{warning}</span>))}
          </div>
        </div>
    );

    render() {
        return (
        <StyledForm>
            {/* <TextInput label="firstname" placeholder="Required" type="text"/>
            <TextInput label="firstname" placeholder="Required" type="text"/>
            <TextInput label="lastname" placeholder="Required" type="text"/>
            <DateInput label="lastname" />
            <RadioInput label="gender" options={["female", "male"]} />
            <TextInput label="email" placeholder="Required" type="text"/> */}
            {/* <StyledField 
                label="mobile" 
                name="mobile"
                placeholder="Required" 
                type="text"
                component={TextInput}
            />
            <StyledField 
                label="firstname"
                name="firstname" 
                placeholder="Required" 
                type="text"
                component={TextInput}
            />
            <StyledField 
                label="lastname"
                name="lastname" 
                placeholder="Required" 
                type="text"
                component={TextInput}
            /> */}
            {/* <StyledField 
                label="birthday"
                name="birthday" 
                component={DateInput}
            />
            <StyledField 
                label="gender"
                name="gender" 
                component={RadioInput}
                options={["female", "male"]}
            /> */}
            <StyledField 
                label="email"
                name="email" 
                placeholder="Required" 
                type="text"
                component={this.renderField}
                validate={required("a")}
            />
            {/* <Field
                name="username"
                type="text"
                component={this.renderField}
                label="Username"
                validate={required}

            /> */}
            <ButtonContainer>
                <StyledButton
                    type="submit"
                >Register</StyledButton>
            </ButtonContainer>
        </StyledForm>
        );
    }
}

export default reduxForm({
    form: 'registration'
  })(RegistrationForm);
